mapper.insert:
 INSERT INTO t_user
(
  email
, pw
, nm
, nick
, tel
, authCd
, provider
)
VALUES
(
   #{email}
 , #{pw}
 , #{nm}
 , #{userNick}
 , #{tel}
 , #{authCd}
 , #{provider}
 )

mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

mapper.insert:
INSERT INTO t_user_profile
(
  iuser
, profileImg
)
VALUES
(
  #{iuser}
, #{profileImg}
)

mapper.update:
UPDATE t_user


        profileImg = #{param.profileImg}


    , nick = #{userNick}


WHERE iuser    = #{param.iuser}

UPDATE t_user
SET nick    = #{userNick}
WHERE iuser = #{iuser}

mapper.select:
SELECT email
      ,pw
      ,nm
      ,nick AS userNick
      ,tel
FROM  t_user
WHERE 1 = 1

    AND email = #{email}


    AND nick = #{userNick}


    AND tel  = #{tel}
