mapper.insert:
 INSERT INTO t_user
(
  email
, pw
, nm
, nick
, tel
, authCd
)
VALUES
(
   #{email}
 , #{pw}
 , #{nm}
 , #{nick}
 , #{tel}
 , #{authCd}
 )

mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

mapper.insert:
INSERT INTO t_board
(
  iuser
, profileImg
)
VALUES
(
  ${iuser}
, ${profileImg}
)

mapper.update:
UPDATE t_user
SET profileImg = #{profileImg}
WHERE iuser    = #{iuser}
